 */
class PayAction extends BaseAction{
    /**
     * 检测操作扣费情况01111
     */
    public function do_pay_check(){
        if(!$this->is_legal_request())       //是否合法请求
            return;
        $service = new PackageService();
        $result  = $service->get_user_operate_pay_info(AccountInfo::get_user_id(), $_POST['mid'], $_POST['uid'], AccountInfo::get_package());
        if(empty($result)){
            echo jsonp_encode(false);
        }
        else{
            echo jsonp_encode(true, $result);
        }
    }
}
